import React from 'react';
// apollo
import { useMutation } from '@apollo/react-hooks';
import { REGISTER_USER } from 'queries';
// components
import TextField from 'components/TextField';
import Button from 'components/Button';
import Modal from 'components/Modal';
// hooks
import useValidation, {
    isRequired,
    isEmail,
    isSameValueWith,
    lengthBetween,
} from 'hooks/useValidation';
// constants
const userNameError = '이름을 입력하세요.';
const emailNameError = 'email을 입력하세요.';
const passwordError = '비밀번호를 입력하세요.';
const passwordConfirmError = '비밀번호를 확인해주세요.';

const configs = {
    fields: {

    }

    // isAccepted: {
    //     default: false,
    //     rules: [[isTrue, '동의해주세요.']],
    // },
};

const Register = props => {
    const [registerUser, { loading, error }] = useMutation(REGISTER_USER);

    const submitForm = async context => {
        await registerUser({
            variables: {
                name: context.values['username'],
                email: context.values['email'],
                password: context.values['password'],
            },
        })
            .then(() => {
                props.history.push('/signin');
            })
            .catch(error => {
                G.log('Error: ', error.message);
            });
    };

    const { isSubmitted, getFieldProps, handleSubmit } = useValidation(
        configs,
        submitForm,
    );
    G.log('Signup formState', error);

    return (
        <div className="wrap">
            <form onSubmit={handleSubmit} noValidate>
                <TextField
                    placeholder="User Name (First + Last Name)"
                    title="User Name"
                    {...getFieldProps('username')}
                />
                <br />
                <TextField
                    placeholder="Email"
                    title="User Email"
                    {...getFieldProps('email')}
                />
                <br />
                <TextField
                    type="password"
                    placeholder="Password"
                    title="Password"
                    {...getFieldProps('password')}
                />
                <br />
                <TextField
                    type="password"
                    placeholder="Repeat Password"
                    title="Repeat password"
                    {...getFieldProps('passwordConfirm')}
                />
                <br />
                <Button
                    type="submit"
                    content={`Register`}
                    primary
                    block
                    loading={loading}
                    disabled={isSubmitted && !error}
                />
            </form>
            {error && (
                <Modal isVisible={!!error}>
                    <div className="text-center">{error && error.message}</div>
                </Modal>
            )}
        </div>
    );
};

export default Register;
